     */
    constructor(agentId, agentName, socket, model, askHuman){

        super('Sbgnviz-Interface-Agent', agentId, agentName, socket, model);

        let self = this;

        self.askHuman = askHuman;


        setTimeout(() =>{

            // self.tm.sendMsg({0: 'tell', content: ['start-conversation']});
